import React from 'react';


//components
import QuestionInput from './QuestionInput';

const QuestionReply = (props) => {


    return (

        <div className="question-replies">

            <div className="question-reply">

                <div className="reply-user-photo">
                    <img src={props.replyDetails.image} alt="User's profile picture" />
                </div>
                <div className="reply-content">
                    <div className="reply">
                        {props.replyDetails.reply}
                    </div>
                    <div className="reply-time">
                        {props.replyDetails.replyDate}
                    </div>
                </div>

            </div>
            <QuestionInput inputDetails={{ "image": "https://randomuser.me/api/portraits/women/32.jpg", "placeholder": "Ask a question" }} />
        </div>
    )


}

export default QuestionReply;
