import React from "react"
import {useSelector, useDispatch} from "react-redux"

import {useState, useEffect} from "react"
import axios from "axios"

const [users, setUsers] = useState("")
const [isLoading, setisLoading] = useState(false)

useEffect(() => {
  axios
    .get(
      "https://cors-anywhere.herokuapp.com/https://us-central1-hammer-app.cloudfunctions.net/api/users"
    )
    .then((response) => {
      setUsers(response.data)
      setisLoading(true)
      console.log(response.data)
    })
    .catch((err) => console.log(err))
}, [])

const dispatch = useDispatch()

const LeftFeed = () => {
  const users = useSelector((state) => state.getUsersReducer.users)
  const isLoading = useSelector((state) => state.getUsersReducer.isLoading)

  if (!isLoading) {
    return (
      <div className="left-feed">
        <div className="text-bold text-main">Followers</div>
        <p>Loading users..</p>
      </div>
    )
  } else {
    return (
      <div className="left-feed">
        <div className="text-bold text-main">Followers</div>
        {users.map((user) => (
          <p key={user.userId}>
            {" "}
            <a href="/">{user.firstName}</a>
          </p>
        ))}
      </div>
    )
  }
}

export default LeftFeed
