import React from 'react';

import { useDispatch, useSelector } from 'react-redux'

import newPost from '../actions/newPost'
//components



const PostInput = (props) => {

    const dispatch = useDispatch();


    return (



        <div className="post-input-box">
            <div className="post-input-area">
                <div className="user-profile-pic">
                    <img src={props.inputUser.image} alt="user " />
                </div>
                <div className="post-input">
                    <textarea placeholder={props.inputUser.placeholder}></textarea>

                </div>

            </div>

            <button className="mainCTA" onClick={(evt) => dispatch(newPost(evt.target.querySelector('textarea').value))}>{props.inputUser.buttonLabel}</button>
        </div>


    )

}
export default PostInput;
