const intitalState = {
  users: [],
  isLoading: true,
}

const getUsersReducer = (state = intitalState, action) => {
  switch (action.type) {
    case "LOAD_USERS":
      return (state = {
        users: action.payload.users,
        isLoading: action.payload.isLoading,
      })
    default:
      return state
  }
}
export default getUsersReducer
