import React from 'react';


//components
import TopicTag from './TopicTag';
import QuestionReply from './QuestionReply';

const QuestionPost = (props) => {


    return (

        <div className="question-post">
            <div className="question-user-photo">
                <img src={props.questionDetails.image} alt="User's profile picture" />
            </div>
            <div className="question-input">
                Ask a question ...
            </div>
            <div className="question-content">
                <div className="question-title">
                    {props.questionDetails.question}
                </div>
                <div className="question-detail">
                    {props.questionDetails.questionDetail}
                </div>
                <div className="question-topic-tags">
                    {
                        // looping tags as per avilable
                        props.questionDetails.questionTag.map((tagstuffs, id) => (
                            <TopicTag key={id} tagDetails={tagstuffs} />
                        ))

                    }
                </div>
                <div className="question-post-date">{props.questionDetails.postedDate}</div>

                {
                    // looping replies
                    props.questionDetails.reply.map((reply, id) => (
                        <QuestionReply key={id} replyDetails={reply} />

                    ))

                }


            </div>

        </div>

    )


}

export default QuestionPost;
